import styled from 'styled-components';


export const DivInput = styled.div`
    display: flex;
    align-items: flex-start;
`;


export const InputNumber = styled.input`
    width: 3rem;

    margin: 0 0.3rem;
    height: 1.8rem;

`;

export const ButtonUpDown = styled.button`
    color: white;
    font-size: 1.6rem;
    background-color: coral;
    border: none;
    border-radius: 8px;
    width: 1.8rem;
    height: 1.8rem;
    

`;
