<div>
<button>-</button>
       <input 
                type="number" 
                id="numPages" 
                name="numPages" 
                min="1" 
                defaultValue="1" 
                onChange={props.handle} 
                value={props.numPagesValue}
            />
<button>+</button>            
</div>
} 

