function App() {


const [budget, setBudget ] = useState([ //Intent en un sol array tots els checkboxes, però aleshorres no sé com que el handleChange copiï l'array i en canviï un valor, pq no sé com dir.li que hi entri
    {
      webPage: false
    },

    {
      seoConsulting: false
    },

    {
      googleAdsCampaign: false
    }
    ]
);


function handleChange(event) {
  console.log(event.target)
//    console.log("name:", name, "checked:", checked);
  setBudget(prevBudget => {
    const { name, checked } = event.target;
const indexCheckboxChecked= budget.findIndex(element => element.name === name );
 console.log("Clicat:", budget[indexCheckboxChecked].name)
    return [](
      [
   ...prevBudget,


    budget[indexCheckboxChecked][name] = checked //he d'accedir a la posició de l'array

    ]
    )

  })
}

  return (
   <>

<form>

<input
  type="checkbox"
  id="webPage"
  checked={budget[0].webPage}
  onChange={handleChange}
  name="webPage"
  value="500"
/>
<label htmlFor="webPage">Una pàgina web (500€)</label>
<input
  type="checkbox"
  id="seoConsulting"
  checked={budget[1].seoConsulting}
  onChange={handleChange}
  name="seoConsulting"
  value="300"
/>
<label htmlFor="seoConsulting">Una consultoria SEO (300€)</label>
<input
  type="checkbox"
  id="googleAdsCampaign"
  checked={budget[2].googleAdsCampaign}
  onChange={handleChange}
  name="googleAdsCampaign"
  value="200"
/>
<label htmlFor="googleAdsCampaign">Una campanya de Google Ads (200€)</label>
</form>
<div>Preu: {budget[0].webPage ? 500 : 0} €</div>
   </>
  );
}

export default App;
