import { useState, useEffect } from "react";
import Panell from "./components/Panell/panell";


function App() {

  const [servicePortfolio, setServicePortfolio] = useState(
    [ 
      {
        name: 'webPage',
        webPage: false,
        price: 500
      },
      {
        name: 'seoConsulting',
        seoConsulting: false,
        price: 300
      },
      {
        name: 'googleAdsCampaign',
        googleAdsCampaign: false,
        price: 200
      }
    ]
  );

  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    setTotalPrice(
        (servicePortfolio[0].webPage ? servicePortfolio[0].price : 0) +
        (servicePortfolio[1].seoConsulting ? servicePortfolio[1].price : 0) +
        (servicePortfolio[2].googleAdsCampaign ? servicePortfolio[2].price : 0)
    )
  }, [servicePortfolio]); 

  function handleChange(event) {
    setServicePortfolio(prevServicePortfolio => {
      const { name, checked } = event.target;
      const indexCheckedBox = servicePortfolio.findIndex(element => element.name === name );

      return [
        ...prevServicePortfolio,
        servicePortfolio[indexCheckedBox][name] = checked     
      ]
    })
  }

  return (   
    <>

      <p>Quin/s servei/s necessites?</p>
      <form>
          <p><input
          type="checkbox"
          id="webPage"
          checked={servicePortfolio[0].webPage}
          onChange={handleChange}
          name="webPage"
        />
        <label htmlFor="webPage">Una pàgina web (500€)</label>
        </p> 
        <div><Panell /></div>
        <p>
        <input
          type="checkbox"
          id="seoConsulting"
          checked={servicePortfolio[1].seoConsulting}
          onChange={handleChange}
          name="seoConsulting"
        />
        <label htmlFor="seoConsulting">Una consultoria SEO (300€)</label>
        </p> 
        <p>
        <input
          type="checkbox"
          id="googleAdsCampaign"
          checked={servicePortfolio[2].googleAdsCampaign}
          onChange={handleChange}
          name="googleAdsCampaign"
        />
        <label htmlFor="googleAdsCampaign">Una campanya de Google Ads (200€)</label>
        </p> 
      </form>
      <p>Preu: {totalPrice}€</p>
   </>
  );
}

export default App;
