function App() {

const [budget, setBudget ] = useState([


    {
      webPage: false
    }
    ]
);


function handleChange(event) {
  setBudget(prevBudget => {
    const { name, checked } = event.target;
    return {
      ...prevBudget,
      [name]: checked
    }
  })
}

  return (
   <>

<form>

<input
  type="checkbox"
  id="webPage"
  checked={budget.webPage}
  onChange={handleChange}
  name="webPage"
/>
<label htmlFor="webPage">Pàgina web</label>
<input
  type="checkbox"
  id="seoConsulting"
  checked={budget.seoConsulting}
  onChange={handleChange}
  name="seoConsulting"
/>
<label htmlFor="webPage">Una consultoria SEO</label>
<input
  type="checkbox"
  id="webPage"
  checked={budget.webPage}
  onChange={handleChange}
  name="webPage"
/>
<label htmlFor="webPage">Pàgina web</label>
</form>
<div>Preu: {budget.webPage ? 500 : 0} €</div>
   </>
  );
}

export default App;
