import styled from 'styled-components';
import image1 from '../../assets/img/pexels-dominika-roseclay-1036848.jpg';

export const WelcomeLayout = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    background-image: radial-gradient(circle closest-side at 45% 65%, rgba(86, 150, 224, 0.8), rgba(145, 179, 250, 0.5) 20%, rgba(158, 209, 252, 0.3) 40%, rgba(145, 173, 248, 0.2)), url(${image1});
    background-repeat: no-repeat;
    background-size: cover;

    width: 100vw;
    height: 100vh;


`;

export const WelcomeContent = styled.div`

    background-color: white;
    box-shadow: 0.5rem 1rem 2rem #888888;
    border-radius: 1rem;
    width: 50%;
    height: 33rem;
    padding: 4rem;

`;


export const WelcomeButtom = styled.button`

padding: 0%.5rem;;
`;
