import styled from 'styled-components';
import image1 from '../../assets/img/pexels-dominika-roseclay-1036848.jpg';

export const WelcomeLayout = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    background-image: radial-gradient(circle closest-side at 25% 75%, rgba(255, 255, 255, 0.8), rgba(255, 255, 255, 0.5) 20%, rgba(255, 255, 255, 0.3) 40%, rgba(255, 255, 255, 0.2)), url(${image1});
    background-repeat: no-repeat;
    background-size: cover;

    width: 100vw;
    height: 100vh;


`;

export const WelcomeContent = styled.div`

background-color: white;
box-shadow:  5px 10px #888888;
width: 100%;
height: 100vh;

`;
