import { useState, useEffect } from "react";

function App() {

  const [budget, setBudget ] = useState(
    [ 
      {
        name: 'webPage',
        webPage: false,
        value: 500
      },
      {
        name: 'seoConsulting',
        seoConsulting: false,
        value: 300
      },
      {
        name: 'googleAdsCampaign',
        googleAdsCampaign: false,
        value: 200
      }
    ]
  );

  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    setTotalPrice(
        (budget[0].webPage ? budget[0].value : 0) +
        (budget[1].seoConsulting ? budget[1].value : 0) +
        (budget[2].googleAdsCampaign ? budget[2].value : 0)
    )
  }, [budget]); 

  function handleChange(event) {
    setBudget(prevBudget => {
      const { name, checked } = event.target;
      const indexCheckboxChecked = budget.findIndex(element => element.name === name );

      return [
        ...prevBudget,
        budget[indexCheckboxChecked][name] = checked     
      ]
    })
  }

  return (   
    <>
      <p>Què necessites?</p>
      <form>
          <p><input
          type="checkbox"
          id="webPage"
          checked={budget[0].webPage}
          onChange={handleChange}
          name="webPage"
          value="500"
        />
        <label htmlFor="webPage">Una pàgina web (500€)</label>
        </p> 
        <p>
        <input
          type="checkbox"
          id="seoConsulting"
          checked={budget[1].seoConsulting}
          onChange={handleChange}
          name="seoConsulting"
          value="300"
        />
        <label htmlFor="seoConsulting">Una consultoria SEO (300€)</label>
        </p> 
        <p>
        <input
          type="checkbox"
          id="googleAdsCampaign"
          checked={budget[2].googleAdsCampaign}
          onChange={handleChange}
          name="googleAdsCampaign"
          value="200"
        />
        <label htmlFor="googleAdsCampaign">Una campanya de Google Ads (200€)</label>
        </p> 
      </form>
      <p>Preu: {totalPrice}€</p>
   </>
  );
}

export default App;
