function App() {

const [budget, setBudget ] = useState(
    {
      webPage: true
    }
);


function handleChange(event) {
  setBudget(prevBudget => {
    const {name, value } = event.target 
    return {
      ...prevBudget,
      [name]: event.target.value
    }
  })
}

  return (
   <>

<form>

<input
  type="checkbox"
  id="webPage"
  checked={budget.webPage}
  onChange={handleChange}
/>
<label htmlFor="webPage">Pàgina web</label>
</form>

   </>
  );
}

export default App;
