import styled from 'styled-components';

export const InputNumber = styled.input`
    width: 3rem;
    padding-left: 0.1rem;
    margin: 0 0.3rem;

`;

export const ButtonUpDown = styled.button`
    color: white;
    background-color: orange;
    border: none;
    border-radius: 10px;

`;
